`,(0,s.jsxDEV)(n.pre,{children:(0,s.jsxDEV)(n.code,{className:"language-python",children:`# TensorFlow audio feature extraction
def extract_audio_features(audio_file):
    """Extract musical features for recommendation engine"""
    y, sr = librosa.load(audio_file)

    features = {
        'tempo': librosa.beat.tempo(y, sr=sr)[0],
        'spectral_centroid': np.mean(librosa.feature.spectral_centroid(y=y, sr=sr)),
        'mfcc': np.mean(librosa.feature.mfcc(y=y, sr=sr, n_mfcc=20), axis=1),
        'energy': np.mean(librosa.feature.rms(y=y)),
        'key': estimate_key(y, sr)
    }

    # Generate embedding vector
    embedding = audio_model.predict(features)
    return embedding
